import React from "react";
import './Style.css';

function PostSliderComponent() {
    return <>
    <div className="post-slider">
      <h1 className="slider-title">Trending Posts</h1>

      <i className="fas fa-chevron-left prev"></i>
      <i className="fas fa-chevron-right next"></i>

      <div className="post-wrapper">
        {/* Repeat the following block for each post */}
        <div className="post">
          <img src="" alt="" className="slider-image" />
          <div className="post-info">
            <h4><a href="single.html">One day your life will flash before your eyes</a></h4>
            <i className="fas fa-user"><span>Suraj Gude</span></i>
            &nbsp;
            <i className="fas fa-calendar"><span>Aug 29, 2023</span></i>
          </div>
        </div>
        {/* End of post block */}
      </div>
    </div>
    </>
}

export default PostSliderComponent;
